def main():
    parse_start(START_URL)
    with tqdm(total=MAX_COUNT) as pbar:
        while not q.empty() and len(articles) < MAX_COUNT:
            try:
                article = parse_article(q.get_nowait())
                requests.put(elastic_search_url.format(len(articles)), json.dumps(article), headers={
                    'Content-Type': 'application/json'
                })
            except Exception as e:
                print(e)
            else:
                pbar.update(1)

    with open('data.json', 'w') as outfile:
        json.dump(articles, outfile)

        # x = articles
